import React, { useState, useEffect } from "react";
import axios from "axios";

export default function Tv() {
  const [trendingTv, setTrendingTv] = useState([]);
  let num = new Array(13).fill(1).map((element, i) => i + 1);


  async function getTv(page) {
    try {
      const response = await axios.get(
        `https://api.themoviedb.org/3/tv/1399/videos?api_key=3f69a27835865c05f6ed1c043a44735a&include_adult=false&include_video=false&language=en-US&page=${page}&sort_by=popularity.desc`
      );
      setTrendingTv(response.data.results);
      console.log(response.data.results);
    } catch (error) {
      console.error("Error fetching TV show videos:", error);
    }
  }

    useEffect(() => {
      getTv(num);
    }, []);

  return (
    <div>
        <div className="row justify-content-center">
            <div className="col-md-2">
              {/* <Link t> */}
                <img
                  className="w-100"
                  src={"https://image.tmdb.org/t/p/w500"}
                  alt=""
                />
                <h3 className="h6 my-3">title</h3>
              {/* </Link> */}
            </div>
          <nav className="d-flex justify-content-center my-5">
            <ul className="pagination pagination-sm">
                <li
                  key={num}
                  onClick={() => getTv(num)}
                  className="page-item"
                >
                  <a className="page-link" href="#">
                    {num}
                  </a>
                </li>
            </ul>
          </nav>
        </div>
        <div className="vh-100 d-flex justify-content-center align-items-center">
          <i className="fas fa-spinner fa-spin"></i>
        </div>
    </div>
  );
}
