import React, { useState } from "react";
import axios from "axios";

export default function Tv() {
  const [trendingTv, setTrendingTv] = useState([]);
  const apiKey = "3f69a27835865c05f6ed1c043a44735a";

  async function getTv() {
    try {
      const response = await axios.get(
        `https://api.themoviedb.org/3/tv/?api_key=${apiKey}&language=en-US`
      );
      setTrendingTv(response.data.results); // Correctly set the state with the data
      console.log(response.data.results); // Log the results to the console
    } catch (error) {
      console.error("Error fetching TV show videos:", error);
    }
  }

  return (
    <div>
      <h1 onClick={getTv}>Get TV Show Videos</h1>
      <div>
        {trendingTv.length > 0 ? (
          <ul>
            {trendingTv.map((video) => (
              <li key={video.id}>
                <h3>{video.name}</h3>
                <iframe
                  width="560"
                  height="315"
                  src={`https://www.youtube.com/embed/${video.key}`}
                  title={video.name}
                  frameBorder="0"
                  allowFullScreen
                ></iframe>
              </li>
            ))}
          </ul>
        ) : (
          <p>No videos available</p>
        )}
      </div>
    </div>
  );
}
